INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 2, '2013-09-30 20:57:52', '2013-09-30 20:57:52', 'Bienvenido a WordPress. Esta es tu primera entrada. Edítala o bórrala, ¡y comienza a publicar!.', '¡Hola mundo!', '', 'publish', 'open', 'open', '', 'hola-mundo', '', '', '2013-09-30 20:57:52', '2013-09-30 20:57:52', '', 0, 'http://metalurgica-smr.com.ar/?p=1', 0, 'post', '', 1),
(2, 2, '2013-09-30 20:57:52', '2013-09-30 20:57:52', 'Esto es una página de ejemplo. Es diferente a una entrada porque permanece fija en un lugar y se mostrará en la navegación de tu sitio (en la mayoría de los temas). La mayoría de la gente empieza con una página de Acerca de, que les presenta a los potenciales visitantes del sitio. Podría ser algo como esto::\n\n<blockquote>¡Hola! Soy mensajero por el día, aspirante a actor por la noche, y este es mi blog. Vivo en Madrid, tengo un perrazo llamado Duque y me gustan las piñas coladas (y que me pille un chaparrón)</blockquote>\n\n...o algo así:\n\n<blockquote>La empresa XYZ se fundó en 1971 y ha estado ofreciendo "cosas" de calidad al público desde entonces. Situada en Madrid, XYZ emplea a más de 2.000 personas y hace todo tipo de cosas sorprendentes para la comunidad de Madrid.</blockquote>\n\nSi eres nuevo en WordPress deberías ir a <a href="http://metalurgica-smr.com.ar/wp-admin/">tu escritorio</a> para borrar esta páginay crear páginas nuevas con tu propio contenido. ¡Pásalo bien!', 'Página de ejemplo', '', 'publish', 'open', 'open', '', 'pagina-ejemplo', '', '', '2013-09-30 20:57:52', '2013-09-30 20:57:52', '', 0, 'http://metalurgica-smr.com.ar/?page_id=2', 0, 'page', '', 0),
(3, 2, '2013-09-30 20:58:25', '0000-00-00 00:00:00', '', 'Borrador automático', '', 'auto-draft', 'open', 'open', '', '', '', '', '2013-09-30 20:58:25', '0000-00-00 00:00:00', '', 0, 'http://metalurgica-smr.com.ar/?p=3', 0, 'post', '', 0),
(4, 2, '2013-09-30 21:00:15', '0000-00-00 00:00:00', '', 'Borrador automático', '', 'auto-draft', 'open', 'open', '', '', '', '', '2013-09-30 21:00:15', '0000-00-00 00:00:00', '', 0, 'http://metalurgica-smr.com.ar/?p=4', 0, 'post', '', 0),
